    private void btnSalvarCadastroFuncionarioActionPerformed(java.awt.event.ActionEvent evt) {                                                             
        String nome = txtNomeFuncionario.getText();
        int idade = Integer.parseInt(txtIdadeFuncionario.getText());
        double salario = Double.parseDouble(txtSalarioFuncionario.getText());
        String cargo = cbCargo.getSelectedItem().toString();
        String usuario = txtUsuarioFuncionario.getText();
        int senha = Integer.parseInt(txtSenhaFuncionario.getText());
        
        if (cargo.equals("Gerente")) {
            adm.cadastrarAdmin(nome, idade, salario, cargo, usuario, senha);
        }
        if (cargo.equals("Atendente")) {
            adm.cadastrarAtendente(nome, idade, salario, cargo, usuario, senha);
        }
        JOptionPane.showMessageDialog(null, "Funcionario Cadastrado");

        //limpar as caixas
        txtNomeFuncionario.setText("");
        txtIdadeFuncionario.setText("");
        txtSalarioFuncionario.setText("");
        txtUsuarioFuncionario.setText("");
        txtSenhaFuncionario.setText("");
    }                                                            

