    private void btnSalvarCPActionPerformed(java.awt.event.ActionEvent evt) {                                            
        String nome = txtNomeProduto.getText();
        int quantidade = Integer.parseInt(txtQuantidadeProduto.getText());
        float preco = Float.parseFloat(txtPrecoProduto.getText());
        int tamanho = Integer.parseInt(cbTamanhoProduto.getSelectedItem().toString());
        char tipo = '\n';
        if (rbModeloMasculino.isSelected()) {
            tipo = 'M';
        } else if (rbModeloFeminino.isSelected()) {
            tipo = 'F';
        }
        String dataCadastro = txtDataCadastro.getText();
        
        adm.cadastrarProduto(nome, quantidade, preco, tamanho, tipo, dataCadastro);
        JOptionPane.showMessageDialog(null, "Produto Cadastrado");

        //limpar caixas
        txtNomeProduto.setText("");
        txtQuantidadeProduto.setText("");
        txtPrecoProduto.setText("");
        txtDataCadastro.setText("");
        if (rbModeloMasculino.isSelected()) {
            rbModeloMasculino.setSelected(false);
        }
        if (rbModeloFeminino.isSelected()) {
            rbModeloFeminino.setSelected(false);
        }
    }                                           

