    public void popularTableVenda() {
        javax.swing.table.DefaultTableModel model = (javax.swing.table.DefaultTableModel) jTable1.getModel();
        model.setNumRows(0);
        
        try {
            if (cbPeriodoVendas.getSelectedItem().toString().equals("3 meses")) {
                resultSet = adm.gerarRelatorio(3);
            } else if (cbPeriodoVendas.getSelectedItem().toString().equals("6 meses")) {
                resultSet = adm.gerarRelatorio(6);
            } else if (cbPeriodoVendas.getSelectedItem().toString().equals("9 meses")) {
                resultSet = adm.gerarRelatorio(9);
            } else {
                System.out.println("erro");
            }
            
            while (resultSet.next()) {
                model.addRow(new Object[]{
                    resultSet.getString(1),
                    resultSet.getString(2),
                    resultSet.getString(3),
                    resultSet.getString(4),
                    resultSet.getString(5),
                    resultSet.getString(6)
                
                });
            }
        } catch (SQLException erro) {
            JOptionPane.showMessageDialog(null, erro);
        }
        
    }
    
