    public void popularJTable() {
        javax.swing.table.DefaultTableModel model = (javax.swing.table.DefaultTableModel) jTable2.getModel();
        model.setNumRows(0);
        Atendente atd = new Atendente();
        
        try {
            if (jComboBox1.getSelectedItem().toString().equals("ordem crescente")) {
                resultSet = atd.getProdutos("Select * from sneakerstore.Produto order by idproduto asc;");
            } else if (jComboBox1.getSelectedItem().toString().equals("ordem decrescente")) {
                resultSet = atd.getProdutos("Select * from sneakerstore.Produto order by idproduto desc;");
            } else if (jComboBox1.getSelectedItem().toString().equals("data de cadastro")) {
                resultSet = atd.getProdutos("Select * from sneakerstore.Produto order by dt_cadastro asc;");
            } else {
                System.out.println("erro");
            }
            
            while (resultSet.next()) {
                model.addRow(new Object[]{
                    //retorna os dados da tabela do BD, cada campo e um coluna.
                    resultSet.getString(1),
                    resultSet.getString(2),
                    resultSet.getString(3),
                    resultSet.getString(4),
                    resultSet.getString(5),
                    resultSet.getString(6),
                    resultSet.getString(7)
                
                });
            }
        } catch (SQLException erro) {
            JOptionPane.showMessageDialog(null, erro);
        }
        
    }

